INSERT OVERWRITE TABLE mobile_active_logged_users_report select device.platform as platform, application.site_id as siteid, usr.user_id as userid, TO_DATE(ds) as activitydate from tracks where ds >= '@param01' and ds < '@param02' and usr.user_id is not null and device.platform in ('/web/mobile', '/mobile/android', '/mobile/ios', '/web/desktop') group by device.platform, application.site_id, usr.user_id, TO_DATE(ds)